mapper.select:
SELECT spot_id, longitude, latitude, spot_name, realtime_people, suggested_people
FROM tb_spot
WHERE longitude between #{minLongitude} and #{maxLongitude}
and latitude between #{minLatitude} and #{maxLatitude}
ORDER BY longitude ASC, latitude ASC

SELECT spot_id, longitude, latitude, spot_name, realtime_people, suggested_people
FROM tb_spot
WHERE spot_id = #{spotId}

mapper.update:
UPDATE tb_spot

    suggested_people = #{suggestedPeople},
    longitude = #{longitude},
    latitude = #{latitude},
    spot_name = #{spotName}

WHERE spot_id = #{spotId}

UPDATE tb_spot
SET realtime_people = realtime_people-1
WHERE spot_id in (SELECT spot_id FROM tb_user_location where user_id = #{userId});
UPDATE tb_user_location

    spot_id = #{spotId},
    update_time = now()

WHERE user_id = #{userId};
UPDATE tb_spot
SET realtime_people = realtime_people+1
WHERE spot_id = #{spotId};

mapper.select:
SELECT COUNT(spot_id)
FROM tb_user_location
WHERE user_id = #{userId}

SELECT spot_id, longitude, latitude, spot_name, realtime_people, suggested_people
FROM tb_spot
WHERE spot_name REGEXP #{spotName}
